import React, { useContext, useState } from 'react';
import { Button, Container, Navbar, Modal } from 'react-bootstrap';
import { CartContext } from '../CartContext';

const NavbarComponent = () => {
    const cart = useContext(CartContext);

    const [ show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const serviceCount = cart.items.reduce((sum, product) => sum + product.quantity, 0);
  return (
    <>
        <Navbar expands="sm">
            <Navbar.Brand href="/">AlbertaDucts Checkout</Navbar.Brand>
            <Navbar.Toggle />
            <Navbar.Collapse className='justify-content-end'>
                <Button onClick={handleShow}>Cart ({serviceCount} Items)</Button>
            </Navbar.Collapse>
        </Navbar>
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Online Checkout</Modal.Title>
            </Modal.Header>
            <Modal.Body closeButton>
                { serviceCount > 0 ?
                    <>
                        <p>item's in your cart:</p>
                        {cart.items.map((currentItem, idx))}
                    </>
                :
                    <h1>There are no items in your cart!</h1>
                }
                <h1>This is the modal body!</h1>
            </Modal.Body>
        </Modal>
    </>
  )
}

export default NavbarComponent;
